let initialState = {
    allPokemons: [],
    pokemons: [],
    types: []
}

function reducer(state=initialState,action){
    switch(action.type){
        case "GET_POKEMONS":
            return {
                ...state, 
                pokemons: action.payload,
                allPokemons: action.payload
            } 
        case "GET_TYPES":
            return {
                ...state, types: action.payload
            }   
        case "FILTER_TYPE":
            const typeFilter = action.payload === "all" ? state.allPokemons : state.allPokemons.filter(el=>el.types[0] === action.payload || el.types[1] === action.payload)
            return{
                ...state,
                pokemons: typeFilter 
            }
        case "FILTER_NAME":
            const nameFilter = "a"
            return {
                ...state,
                pokemons: nameFilter
            }
        case "FILTER_ORIGIN":
            const originFilter = action.payload === "all" ? state.pokemons : action.payload === "api" ? state.pokemons.filter(e=> !e.hasOwnProperty(createdInDb)) : action.payload === "existent" ? state.pokemons.filter(e=>e.hasOwnProperty(createdInDb)) : null
            return{
                ...state,
                pokemons: originFilter
            }
        case "FILTER_ATTACK":
            const attackFilter = "a"
            return {
                ...state,
                pokemons: attackFilter
            }

        default: return state;    
    }
}

export default reducer;
