import React, { useEffect } from "react";
import {useDispatch,useSelector} from "react-redux";
import { useParams } from "react-router-dom";
import {getPokemonById, cleanDetail, getTypes} from "../redux/actions";
import Error from "./Error";
import styles from "./Detailed.module.css";
import {Link} from "react-router-dom";

function Detailed(){

    const {id} = useParams();
    const dispatch = useDispatch();
    const detail = useSelector((state)=>state.detail);

    useEffect(()=>{
        dispatch(cleanDetail())
        dispatch(getPokemonById(id))
    },[dispatch])

    return(
        <div>
            <nav>
                <Link to={"/home"}>
                    <h3>Home</h3>
                </Link>
            </nav>
            <div className={styles.pokemonDetailContainer}>
            {
                detail ? 
                <div className={styles.detailedPokemon}>
                    <p>Pokemon ID: {detail.id}</p>
                    <h1>{detail.name}</h1> 
                    <img src={detail.img} height="200px" width="200px"></img>
                    <div>
                        Stats:
                        <p>HP: {detail.life}</p>
                        <p>Attack: {detail.strength}</p>
                        <p>Defense: {detail.defense}</p>
                        <p>Speed: {detail.speed}</p>
                        <p>Height: {detail.height}</p>
                        <p>Weight: {detail.weight}</p>
                    </div>
                    <p>{detail.types?.map(e=>e).join(" ")}</p>
                </div>
                : <Error/>
            }
            </div>
        </div>
    )
}

export default Detailed;
