import React from "react";
import {useState} from "react";
import styles from "./Paginado.module.css"

function Paginado({currentPage,setCurrentPage,max}){
const [input,setInput] = useState(1)
    function handleRight(){
        setCurrentPage(currentPage+1)
        setInput(input + 1)
    }

    function handleLeft(){
        setCurrentPage(currentPage-1)
        setInput(input - 1)
    }

    function onKeyPress(e){
        e.preventDefault()
        if(e.keyCode === "13"){
            setCurrentPage(parseInt(e.target.value))
            if(parseInt(e.target.value)<1 || parseInt(e.target.value) > Math.ceil(max) || isNaN(parseInt(e.target.value))){
                setInput(1)
                setCurrentPage(1)
            }else{
                setCurrentPage(parseInt(e.target.value))
            }
        }
    }

    function onChange(e){
        setInput(e.target.value)
    }

    return(
        <div className={styles.pagination}>
            <button onClick={()=>handleLeft()} disabled={currentPage === 1} className={styles.leftButton}>Left</button>
            <input onChange={(e)=>onChange(e)} onKeyDown={(e)=>onKeyPress(e)} value={input} name="page" className={styles.input}></input>
            <p className={styles.p}>of {Math.ceil(max)}</p>
            <button onClick={()=>handleRight()} disabled={currentPage >= max} className={styles.rightButton}>Right</button>
        </div>
    )
}

export default Paginado;
