import React from "react";
import styles from "./SearchBar.module.css";
import {useState} from "react";
import { useDispatch } from "react-redux";
import {getPokemonName, cleanPokemons} from "../redux/actions";

function SearchBar(){
    const [input,setInput] = useState("")
    const dispatch = useDispatch()

    function handleChange(e){
        e.preventDefault()
        setInput(e.target.value)
    }

    function getPokemon(e){
        e.preventDefault()
        dispatch(cleanPokemons())
        dispatch(getPokemonName(input))
        setInput("")
    }

    return (
        <div className="container">
            <input onChange={(e)=>handleChange(e)} value={input} className={styles.input} type="text" placeholder="Search pokemon..."></input>
            <button onClick={(e)=>getPokemon(e)} type="submit">Search</button>
        </div>
    ) 
}

export default SearchBar;
