import React from "react";
import {getPokemons,getTypes} from "../redux/actions";
import {useEffect, useState} from "react";
import {useDispatch, useSelector} from "react-redux";
import Card from "./Card";
import {Link} from "react-router-dom";
import styles from "./Home.module.css"

function Home() {
  const dispatch = useDispatch()
  const pokemons = useSelector((state)=>state.pokemons)
  const types = useSelector((state)=>state.types)
  const [currentPage, setCurrentPage] = useState(1)
  const [pokemonsInPage, setPokemonsInPage] = useState(12)


  useEffect(()=>{
    dispatch(getPokemons())
    dispatch(getTypes())
  },[dispatch])

  function handleClick(e){
    e.preventDefault();
    dispatch(getPokemons())
  }


    return (
      <div>
        <div className="filters">
        <select className={styles.filter} value="disabled">
          <option value={""}>Type</option>
          <option value={"all"}>All Types</option>
          {
            types && types.map(e=>{
              return (
                <option value={e.name} key={e.name}>{e.name}</option>
              )
            })
          }
        </select>
        <select className={styles.filter}>
          <option value={"all"}>All</option>
          <option value={"existent"}>Existent</option>
          <option value={"api"}>Created</option>
        </select>
        <select className={styles.filter}>
          <option value={"A-Z"}>A-Z</option>
          <option value={"Z-A"}>Z-A</option>
        </select>
        <select className={styles.filter}>
          <option value={"stronger"}>+Strength</option>
          <option value={"weaker"}>-Strength</option>
        </select>
        </div>

        <button onClick={e=>{handleClick(e)}}>Refresh All Filters</button>

          <div className={styles.pokemonContainer}>
        {
          pokemons.length && pokemons.map((e)=>{
            return (
              <div className="pokemon">
              <Link to={`/home/${e.id}`}>
                <Card
                 name={e.name}
                 img={e.img}
                 types={e.types} 
                  />
              </Link>   
              </div>
            )
          })
        }
        </div>
      </div>
    );
  }

  export default Home;
